    /**
     * Updates the knob value
     *
     * @param knob  : 1..3
     * @param value : 0..100
     */
    public static void sendKnobValue(int knob, int value) {
        MatriboxIIPro.sendControlChange(1, 16 + (knob - 1) * 2, value);
    }

    /**
     * updates the preset volume
     *
     * @param volume : 0..100
     */
    public static void sendPresetVolume(int volume) {
        MatriboxIIPro.sendControlChange(1, 7, volume);
    }

    public static void sendBank(int bank_id) {
        if (bank_id >= 1 && bank_id <= 120) {
            MatriboxIIPro.sendControlChange(1, 0, bank_id);
        }
        else if (bank_id >= 121 && bank_id <= 120) {
            MatriboxIIPro.sendControlChange(1, 0, bank_id - 120);
        }
        else {
            System.err.println("sendBank(bank_id: " + bank_id + ") : 1 <= bank_id <= 240");
        }
    }

